mapper.select:
SELECT COUNT(*)
  FROM member
 WHERE id = #{id}

SELECT ROUND(MONTHS_BETWEEN(sysdate, changeDate)) 
  FROM member 
 WHERE id = #{id}

SELECT no 
  FROM member 
 WHERE id = #{id} AND passwd = #{passwd}

mapper.update:
UPDATE member
   SET phone1 = #{phone1}, phone2 = #{phone2}, phone3 = #{phone3}, email1 = #{email1}, email2 = #{email2}, postcode = #{postcode}, address = #{address}, detailAddress = #{detailAddress}, extraAddress = #{extraAddress}
 WHERE no = #{no} AND passwd = #{passwd}

UPDATE member
   SET passwd = #{newPasswd}, changeDate = SYSDATE
 WHERE no = #{no}
