mapper.select:
SELECT COUNT(*) totalRecord
  FROM diary
 WHERE 1=1 
   AND tbl = #{tbl}
   AND memberNo = #{memberNo}

	SELECT *
	  FROM (SELECT rownum rnum, basic.*
	  		  FROM (SELECT *
	  		  		  FROM diary
	 		 		 WHERE 1=1
	 		 		   AND tbl = #{tbl}
	 		 		   AND memberNo = #{memberNo}

  		  		  ORDER BY no DESC) basic
  		   )
  	 WHERE rnum BETWEEN #{startRecord} AND #{lastRecord}

	SELECT *
	  FROM diary
  	 WHERE no = #{no}
  	   AND tbl = #{tbl}
  	   AND memberNo = #{memberNo}

mapper.update:
UPDATE diary
   SET title = #{title}, content = #{content}
 WHERE no = #{no}
   AND tbl = #{tbl}
   AND memberNo = #{memberNo}

mapper.delete:
DELETE FROM diary
 	  WHERE no = #{no}
 	    AND tbl = #{tbl}
 	    AND memberNo = #{memberNo}
